import React from 'react';
import logo from './logo.svg';
import './App.css';
import ChoosePlayers from './components/choosePlayers.js'
import ChooseXorO from './components/chooseXorO.js'
import Grid from './components/grid.js'
import Login from './components/login.js'
import Signup from './components/signup.js'

class App extends React.Component {

  constructor(){
    super()

    this.state = {
      hasChosenMode: false,
      hasChosenXorO: false,
      toggleGrid: false,
      setX: false,
      setO: false,
      clickedLogin: false,
      clickedSignup: false,
      currentUser: {}
    }
    this.chooseNumberOfPlayers = this.chooseNumberOfPlayers.bind(this)
    this.chooseXorO = this.chooseXorO.bind(this)
    this.resetAll = this.resetAll.bind(this)
    this.chooseX = this.chooseX.bind(this)
    this.chooseO = this.chooseO.bind(this)
    this.login = this.login.bind(this)
    this.signup = this.signup.bind(this)
    this.returnToHomepage = this.returnToHomepage.bind(this)
    this.loginUser = this.loginUser.bind(this)
    this.setCurrentUser = this.setCurrentUser.bind(this)
  }


  loginUser(user){
    fetch("http://localhost:3001/login", {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify({
        email: user.email,
        password: user.password
      })
    })
    .then(res => res.json())
    .then(data => {
      this.setCurrentUser(data)
    })
  }

  setCurrentUser(data){
    this.setState({
      currentUser: data.user
    })
    if(data.jwt){localStorage.token = data.jwt}
    console.log(localStorage.token)
  }















  renderChoosePlayers(){
    if(this.state.hasChosenMode === false){
      return <ChoosePlayers choose={this.chooseNumberOfPlayers}/>
    }
  }

  chooseNumberOfPlayers(){
    this.setState({
      hasChosenMode: true,
      hasChosenXorO: true
    })
  }

  renderChooseXorO(){
    if(this.state.hasChosenXorO === true){
      return <ChooseXorO choose={this.chooseXorO} chooseX={this.chooseX} chooseO={this.chooseO}/>
    }
  }

  chooseXorO(){
    this.setState({
      hasChosenXorO: false,
      toggleGrid: true
    })
  }

  chooseX(){
    console.log("hello x")
    this.setState({
      hasChosenXorO: false,
      toggleGrid: true,
      setX: true
    })
  }

  chooseO(){
    console.log("hello o")
    this.setState({
      hasChosenXorO: false,
      toggleGrid: true,
      setO: true
    })
  }


  renderGrid(){
    if(this.state.toggleGrid === true){
      return <Grid reset={this.resetAll} setX={this.state.setX} setO={this.state.setO}/>
    }
  }

  resetAll(){
    console.log("hello")
    this.setState({
      toggleGrid: false,
      hasChosenMode: false
    })
  }

  login(){
    this.setState({
      clickedLogin: true,
      hasChosenMode: true
    })
  }

  renderLogin(){
    if(this.state.clickedLogin === true){
      return(
        <Login homepage={this.returnToHomepage}
               loginUser={this.loginUser}
          />
      )
    }
  }

  signup(){
    this.setState({
      clickedSignup: true,
      hasChosenMode: true
    })
  }

  renderSignup(){
    if(this.state.clickedSignup === true){
      return(
        <Signup homepage={this.returnToHomepage}/>
      )
    }
  }

  returnToHomepage(){
    this.setState({
      clickedLogin: false,
      clickedSignup: false,
      hasChosenMode: false
    })
  }

  render(){
  return (
    <div className="App">
      {this.renderGrid()}
    <div className="mainDiv">
    <div className="signinDiv">
    <button className="login" onClick={this.login}>Log in</button>
    <button className="signup" onClick={this.signup}>Sign up</button>
    </div>
    <div className="innerDiv">
      {this.renderChoosePlayers()}
      {this.renderChooseXorO()}
      {this.renderLogin()}
      {this.renderSignup()}
    </div>
    </div>
    </div>
  );
}
}

export default App;
